// src/App.js
import React, { useState, useEffect } from "react";
import ProductList from "./";
import Cart from "./Cart";
import ProgressBar from "./CartSummary/CartSummary";
import "./App.css";

const PRODUCTS = [
  { id: 1, name: "Laptop", price: 500 },
  { id: 2, name: "Smartphone", price: 300 },
  { id: 3, name: "Headphones", price: 100 },
  { id: 4, name: "Smartwatch", price: 150 },
];

const FREE_GIFT = { id: 99, name: "Wireless Mouse", price: 0 };
const THRESHOLD = 1000;

function App() {
  const [cart, setCart] = useState([]);
  const [subtotal, setSubtotal] = useState(0);

  useEffect(() => {
    const newSubtotal = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
    setSubtotal(newSubtotal);

    if (newSubtotal >= THRESHOLD && !cart.some(item => item.id === FREE_GIFT.id)) {
      setCart([...cart, { ...FREE_GIFT, quantity: 1 }]);
    } else if (newSubtotal < THRESHOLD) {
      setCart(cart.filter(item => item.id !== FREE_GIFT.id));
    }
  }, [cart]);

  return (
    <div className="app">
      <h1>Shopping Cart</h1>
      <ProgressBar subtotal={subtotal} threshold={THRESHOLD} />
      <ProductList products={PRODUCTS} cart={cart} setCart={setCart} />
      <Cart cart={cart} setCart={setCart} />
    </div>
  );
}

export default App;
