// src/ProductList.jsx
import React from "react";
import "./Products.css";

function ProductList({ products, cart, setCart }) {
  const addToCart = (product) => {
    useEffect(() => {
      const newSubtotal = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
      setSubtotal(newSubtotal);
    
      setCart((prevCart) => {
        const hasFreeGift = prevCart.some(item => item.id === FREE_GIFT.id);
    
        if (newSubtotal >= THRESHOLD && !hasFreeGift) {
          return [...prevCart, { ...FREE_GIFT, quantity: 1 }];
        } 
        
        if (newSubtotal < THRESHOLD && hasFreeGift) {
          return prevCart.filter(item => item.id !== FREE_GIFT.id);
        }
    
        return prevCart;
      });
    }, [cart]); // ✅ Still depends on `cart`, but prevents infinite loop
    
  };
  
  

  return (
    <div className="product-list">
      {products.map((product) => (
        <div key={product.id} className="product">
          <div>
          <h1>{product.name}</h1>
          <p>Price: ${product.price}</p>
          </div>
          <button className="add-to-cart" onClick={() => addToCart(product)}>
            Add to Cart
          </button>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
